mapper.select:
SELECT signup_activity.actID, response, payment
FROM signup_activity,
     activity
where userID = #{userID}
  and signup_activity.actID = activity.actID
  and activity.startDate > #{date}

mapper.update:
UPDATE activity
SET userJoinedNum=#{userJoinedNum}
WHERE actID = #{actID}

mapper.select:
SELECT startDate, userJoinedNum, capacity
FROM activity
where actID = #{actID}
