mapper.select:
SELECT rental.user_id, rental.rentalID, rental.sexConstraint, rental.floorPlan, rental.description, rental.publishedTime, rental.rentalStartTime, rental.rentalEndTime, rental.price, rental.location, rental.contact, rental.images, user.avatar as sellerAvatar, user.nickname as sellerNickname FROM rental INNER JOIN user on rental.user_id = user.id WHERE rental.price <= #{priceLimit} AND rental.rentalStartTime >= Date_SUB(NOW(), INTERVAL 5 DAY) AND rental.publishedTime IS NOT NULL AND rental.floorPlan in
#{floorplan}
ORDER BY rental.publishedTime DESC LIMIT #{offset}, #{limit}

SELECT rental.user_id, rental.rentalID, rental.sexConstraint, rental.floorPlan, rental.description, rental.publishedTime, rental.rentalStartTime, rental.rentalEndTime, rental.price, rental.location, rental.contact, rental.images, user.avatar as sellerAvatar, user.nickname as sellerNickname FROM rental INNER JOIN user on rental.user_id = user.id WHERE rental.rentalID >= #{offset} AND rental.price <= #{priceLimit} AND rental.rentalStartTime <= Date_ADD(#{startTime}, INTERVAL 5 DAY) AND rental.rentalStartTime >= Date_SUB(#{startTime}, INTERVAL 5 DAY) AND rental.rentalEndTime <= Date_ADD(#{endTime}, INTERVAL 5 DAY) AND rental.rentalEndTime >= Date_SUB(#{endTime}, INTERVAL 5 DAY) AND rental.publishedTime IS NOT NULL AND rental.floorPlan in
#{floorplan}
ORDER BY rental.publishedTime DESC LIMIT #{offset}, #{limit}

mapper.update:
UPDATE user
SET wechat_id = #{contact}
WHERE id = #{userID}

mapper.delete:
DELETE
FROM rental
WHERE rentalID = #{rentalID}
  AND user_id = #{userID}
