mapper.select:
SELECT productID,sellerAvatar,sellerNickname, productTitle,productDescription,productType,time,price,productCondition,contact,imagesJSON,delivery
FROM secondhand
WHERE productID >= #{offset}
ORDER BY secondhand.productID
LIMIT #{limit}

SELECT productID,sellerAvatar,sellerNickname, productTitle,productDescription,productType,time,price,productCondition,contact,imagesJSON,delivery
FROM secondhand
WHERE productID >= #{offset} AND productType = #{productType}
ORDER BY secondhand.productID
LIMIT #{limit}

mapper.update:
UPDATE user 
SET savedProductJSON = #{savedProductJSON}
WHERE userID = #{userID}
