mapper.update:
UPDATE user
open_id = #{openID}, nickname = #{nickname}, avatar = #{avatar}, email = #{email}, is_student = #{isStudent}, subscribe = #{subscribe}, is_blocked = #{isBlocked}, avatar_url = #{avatarURL},
WHERE id = #{id}

UPDATE secondhand
SET time = #{time}
WHERE open_id = #{userID}
  AND productID = #{productID}

UPDATE secondhand
SET time = NULL
WHERE open_id = #{userID}
  AND productID = #{productID}

UPDATE rental
SET publishedTime = #{time}
WHERE open_id = #{userID}
  AND rentalID = #{rentalID}

UPDATE rental
SET publishedTime = NULL
WHERE open_id = #{userID}
  AND rentalID = #{rentalID}
